import React from 'react';
import '../css/CountryDetails.css';
import myCountries from '../countries.json'
import {Link} from 'react-router-dom';

export default function CountryDetails(props) {

  const getCountry = (id) => {
    let found = myCountries.find(singleCountry => {
      return singleCountry.cca3 === id;
    })
    return found
  }

  const getBorders = (borderIDs) => {
    let myNeighbors = borderIDs.map((neighbor) => {
      return(
        <li>
          <Link to={`/details/${neighbor}`}></Link>
        </li>
      )
    })
  }

  const params = props.match.params;
  const foundCountry = getCountry(params.id);

  return (
    <div className="single-country-container">
      <h2>{foundCountry.name.common}</h2>
      <h3>{foundCountry.capital}</h3>
      <h4>{foundCountry.area}</h4>
      <ul>
        {getBorders}
      </ul>
      <p>{foundCountry.borders}</p>
      <Link to="/">Go back</Link>
    </div>
  )
}
