import React from 'react';
import '../css/CountryDetails.css';
import myCountries from '../countries.json'
import {Link} from 'react-router-dom';

export default function CountryDetails(props) {

  const getCountry = (id) => {

    let found = myCountries.find(singleCountry => {
      return singleCountry.c === id;
    })

    return found
  }

  const {params} = props.match;
  const foundCountry = getCountry(params.id);


  return (
    <div className="single-country-container">
      <h2>{foundCountry.name}</h2>
      <h3>{foundCountry.year}</h3>
      <h4>{foundCountry.technologies}</h4>
      <p>{foundCountry.description}</p>
      <Link to="/">Go back</Link>
    </div>
  )
}
